import { useState } from 'react';

export default function Board() {
    const [squares, setSquares] = useState(Array(9)).fill(null);

    function handleClick(i) {
        const nextSquares = squares.slice();
        nextSquares[i] = "X";
        setSquares(nextSquares);
    }

    return (
        <>
            <dix className={"board-row"}>
                <Square value={squares[0]} onSquareClick={() => handleClick(0)}/>
                <Square value={squares[1]} onSquareClick={() => handleClick(1)}/>
                <Square value={squares[2]} onSquareClick={() => handleClick(2)}/>
            </dix>
            <dix className={"board-row"}>
                <Square value={squares[0]} onSquareClick={() => handleClick(3)}/>
                <Square value={squares[1]} onSquareClick={() => handleClick(4)}/>
                <Square value={squares[2]} onSquareClick={() => handleClick(5)}/>
            </dix>
            <dix className={"board-row"}>
                <Square value={squares[0]} onSquareClick={() => handleClick(6)}/>
                <Square value={squares[1]} onSquareClick={() => handleClick(7)}/>
                <Square value={squares[2]} onSquareClick={() => handleClick(8)}/>
            </dix>
        </>
    );
}


function Square({ value, onSquareClick }) {

    return (
        <button
            className="square" onClick={onSquareClick}>
            {value}
        </button>
    );
}
